4. Add performance measurement:
```javascript
// Add to content.js
function measurePerformance() {
  const start = performance.now();

  // Create a MutationObserver to detect when scores appear
  const observer = new MutationObserver(() => {
    const { tomatoMeter, popcornMeter } = extractScores();
    if (tomatoMeter !== null && popcornMeter !== null) {
      // Start timing when scores are detected
      const scoresDetectedTime = performance.now();

      // Set up another observer to detect when our badge appears
      const badgeObserver = new MutationObserver(() => {
        if (document.querySelector('.reel-score-badge')) {
          const badgeRenderedTime = performance.now();
          const renderTime = badgeRenderedTime - scoresDetectedTime;

          console.log(`Reel Score render time: ${renderTime.toFixed(2)}ms`);

          // Report if over budget
          if (renderTime > 200) {
            console.warn(`Render time exceeds 200ms budget: ${renderTime.toFixed(2)}ms`);
          }

          badgeObserver.disconnect();
        }
      });

      badgeObserver.observe(document.body, { childList: true, subtree: true });
      observer.disconnect();
    }
  });

  observer.observe(document.body, { childList: true, subtree: true });
}

// Call this in development/testing builds
if (process.env.NODE_ENV === 'development') {
  measurePerformance();
}
```

Implement error handling and logging:

```javascript
// logger.js
export const LogLevel = {
  ERROR: 'error',
  WARN: 'warn',
  INFO: 'info',
  DEBUG: 'debug'
};

export class Logger {
  constructor(context = 'ReelScore') {
    this.context = context;
    this.enabled = true;
    this.errorCount = 0;
  }

  log(level, message, data = {}) {
    if (!this.enabled) return;

    const logData = {
      context: this.context,
      timestamp: new Date().toISOString(),
      url: window.location.href,
      ...data
    };

    switch (level) {
      case LogLevel.ERROR:
        console.error(`[${this.context}] ${message}`, logData);
        this.errorCount++;
        // Could send to error reporting service here
        break;
      case LogLevel.WARN:
        console.warn(`[${this.context}] ${message}`, logData);
        break;
      case LogLevel.INFO:
        console.info(`[${this.context}] ${message}`, logData);
        break;
      case LogLevel.DEBUG:
        console.debug(`[${this.context}] ${message}`, logData);
        break;
    }
  }

  error(message, data = {}) {
    this.log(LogLevel.ERROR, message, data);
  }

  warn(message, data = {}) {
    this.log(LogLevel.WARN, message, data);
  }

  info(message, data = {}) {
    this.log(LogLevel.INFO, message, data);
  }

  debug(message, data = {}) {
    this.log(LogLevel.DEBUG, message, data);
  }

  getErrorCount() {
    return this.errorCount;
  }
}

// Create a global logger instance
export const logger = new Logger();

// Add global error handler
export function setupGlobalErrorHandling() {
  window.addEventListener('error', (event) => {
    logger.error('Uncaught error', {
      message: event.message,
      filename: event.filename,
      lineno: event.lineno,
      colno: event.colno,
      stack: event.error?.stack
    });
  });

  window.addEventListener('unhandledrejection', (event) => {
    logger.error('Unhandled promise rejection', {
      reason: event.reason?.message || event.reason,
      stack: event.reason?.stack
    });
  });
}
```

