4. Create sample test for score calculation:
```javascript
// src/reelScoreCalculator.test.js
import { calculateReelScore, getReelScoreVisuals } from './reelScoreCalculator';

describe('calculateReelScore', () => {
  test('calculates positive difference correctly', () => {
    expect(calculateReelScore(90, 70)).toBe(20);
  });

  test('calculates negative difference correctly', () => {
    expect(calculateReelScore(70, 90)).toBe(-20);
  });

  test('calculates zero difference correctly', () => {
    expect(calculateReelScore(80, 80)).toBe(0);
  });

  test('returns null when popcornMeter is null', () => {
    expect(calculateReelScore(null, 70)).toBeNull();
  });

  test('returns null when tomatoMeter is null', () => {
    expect(calculateReelScore(70, null)).toBeNull();
  });
});

describe('getReelScoreVisuals', () => {
  test('returns green with thumbs-up for positive score', () => {
    const visuals = getReelScoreVisuals(20);
    expect(visuals.color).toBe('#2ecc71');
    expect(visuals.icon).toBe('thumbs-up');
    expect(visuals.description).toContain('20% higher');
  });

  test('returns red with thumbs-down for negative score', () => {
    const visuals = getReelScoreVisuals(-20);
    expect(visuals.color).toBe('#e74c3c');
    expect(visuals.icon).toBe('thumbs-down');
    expect(visuals.description).toContain('20% lower');
  });

  test('returns gray with neutral icon for zero score', () => {
    const visuals = getReelScoreVisuals(0);
    expect(visuals.color).toBe('#95a5a6');
    expect(visuals.icon).toBe('neutral');
    expect(visuals.description).toContain('agree exactly');
  });
});
